import { useEffect, useState } from "react";
import "./App.css";
import API from "./data/shoes.json";
import axios from "axios";
function App() {
  const [data, setData] = useState([]);
  useEffect(() => {
    API && setData(API.shoes);
  }, []);
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  console.log({ data });
  useEffect(() => {
    const cart = axios.get("http://localhost:5000/getcart");
    setCart(cart);
    console.log({ cart });
    // const getTotal = () => {
    //   const total = cart.reduce((prev, item) => {
    //     return prev + item.price * item.quantity;
    //   }, 0);

    //   setTotal(total);
    // };

    // getTotal();
  }, [cart]);
  const addToCart = async (product) => {
    console.log({ product });
    await axios.post("http://localhost:5000/addcart", { product });
  };

  const increment = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity += 1;
      }
    });

    setCart([...cart]);
    addToCart(cart);
  };

  const decrement = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity === 1 ? (item.quantity = 1) : (item.quantity -= 1);
      }
    });

    setCart([...cart]);
    addToCart(cart);
  };

  const removeProduct = (id) => {
    if (window.confirm("Do you want to delete this product?")) {
      cart.forEach((item, index) => {
        if (item._id === id) {
          cart.splice(index, 1);
        }
      });

      setCart([...cart]);
      addToCart(cart);
    }
  };

  // if (cart.length === 0) {
  //   return (
  //     <h2 style={{ textAlign: "center", fontSize: "5rem" }}>Cart Empty</h2>
  //   );
  // }

  return (
    <div className="App_mainContent_12BYb">
      <div className="App_card_38zmH">
        <div className="App_cardTop_3hHIG">
          <img src="/nike.png" alt="" className="App_cardTopLogo_2ho9K" />
        </div>
        <div className="App_cardTitle_29nyq">Our Products</div>
        <div className="App_cardBody_1tfYc">
          {data &&
            data.map((item) => (
              <div key={item.id} style={{ paddingBottom: "30px" }}>
                <div className="App_shopItem_3FgVU">
                  <div
                    className="App_shopItemImage_341iU"
                    style={{ backgroundColor: `${item.color}` }}
                  >
                    <img src={item.image} alt="" />
                  </div>
                </div>
                <div className="App_shopItemName_1_FJR">{item.name}</div>
                <div className="App_shopItemDescription_1EIVK">
                  {item.description}
                </div>
                <div className="App_shopItemBottom_3401_">
                  <div className="App_shopItemPrice_2SLiG">${item.price}</div>
                  <div
                    className="App_shopItemButton_23FO1"
                    onClick={() => addToCart(item)}
                  >
                    <p>ADD TO CART</p>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </div>
      <div className="App_card_38zmH">
        <div className="App_cardTop_3hHIG">
          <img src="/nike.png" alt="" className="App_cardTopLogo_2ho9K" />
        </div>
        <div className="App_cardTitle_29nyq">
          Your cart<span className="App_cardTitleAmount_17QFR">$89.97</span>
        </div>
        <div className="App_cardBody_1tfYc">
          <div>
            <div className="App_cartItem_lfA9I">
              <div className="App_cartItemLeft_1HqDk">
                <div
                  className="App_cartItemImage_1rLvq cartItemImage"
                  style={{ backgroundColor: "rgb:(77,49,127)" }}
                >
                  <div className="App_cartItemImageBlock_wRE4E">
                    <img
                      src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/1315882/air-zoom-pegasus-36-shield-mens-running-shoe-24FBGb__1_-removebg-preview.png"
                      alt=""
                    />
                  </div>
                </div>
              </div>
              <div className="App_cartItemRight_2LNcC">
                <div className="App_cartItemName_3he6M cartItemName">
                  Nike Air Zoom Pegasus 36 Shield
                </div>
                <div className="App_cartItemPrice_R0sr2 cartItemPrice">
                  $89.97
                </div>
                <div className="App_cartItemActions_13kia">
                  <div className="amount">
                    <button> - </button>
                    <span>1</span>
                    <button> + </button>
                  </div>
                  <div className="App_cartItemRemove_1GiLR cartItemRemove">
                    <img
                      src="data:image/png;base64,"
                      alt=""
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
